#include <stdint.h>
#include <stdio.h>
#include "stm32f4xx.h"
#include "system_stm32f4xx.h"
#include "accel.h"
#include "led.h"
#include "spi.h"
#include "i2c.h"
#include "i2c_lcd.h"


#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

int main(void)
{
	char str[64];
	LIS_Data val;
	SystemInit();
	LedInit(LED_GREEN_PIN);
	LedInit(LED_ORANGE_PIN);
	LedInit(LED_RED_PIN);
	LedInit(LED_BLUE_PIN);
	I2C_Init();
	Lcd_Init();
	SPI_Init();
	LIS_Init();

		while(1) {
			if(LIS_IsDataAvail())
			{
				val = LIS_GetData();
				sprintf(str, "X=%d, Y=%d, Z=%d\r\n", val.x, val.y, val.z);
				Lcd_Puts(LCD_LINE1, str);
				Lcd_Puts(LCD_LINE2, "HARDIK-87298");
				(val.x >= 1000)? LedOn(LED_RED_PIN):LedOff(LED_RED_PIN);
				(val.z >= 1000)? LedOn(LED_BLUE_PIN):LedOff(LED_BLUE_PIN);
			}
			DelayMs(1000);
		}
	return 0;
}

